package String;

public class StringLength {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str = "welcome to java program";//23
		int counter = 1;
		for (int i = 0; i < str.length() - 1; i++) {
			counter++;
			
		}System.out.print("String length is :"+counter);
	}

}
